import React from 'react';
import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';
function MealItemForm() {
  return (
    <form action="" className={classes.form}>
      <input type="text" />
      <Input label="Amount" input={{
        id:'amount',
        type:'numbrer',
        min:'1',
        max:'5',
        
      }}/>
      <button>+ Add</button>
    </form>
  )
}

export default MealItemForm;
