  </ul>
  return (
    <CartModel>
    {cartItems}
    <div className={classes.total}>
      <span>Total Amount</span>
      <span>35.62</span>
    </div>
    <div className={classes.actions}>
      <button className={classes['button-alt']} onClick={closeBackdrop}>Close</button>
      <button className={classes.button}>Order</button>
    </div> 
  </CartModel>
  )
} 

export default Cart;
